import urllib.requestimport reimport matplotlib.pyplot as pltimport numpy as npauthor = input("Please input the name of author: ")print("[ Author: " + author + " ]")number = 0temp = 0Timelist = [0]while number < 1000:	url = "https://arxiv.org/search/?searchtype=author&query=" + author + "&abstracts=show&size=200&order=-announced_date_first&start=" + str(number)	##url = "https://arxiv.org/search/?query=" + author + "&searchtype=author"	content = urllib.request.urlopen(url)	html_str = content.read().decode('utf-8')	number += 200	pattern = '<span class="has-text-black-bis has-text-weight-semibold">originally announced</span>[\s\S]*?</p>'	result = re.findall(pattern, html_str)	i = 0	for r in result:		time = r.split("has-text-black-bis has-text-weight-semibold\">originally announced</span>")[1].split("</p>")[0].strip().split('\s')		time = re.findall(r"\d+\d*",str(time))		Timelist += list(map(int, time))		i += 1		if number == 2000:		print(Timelist)		breakTimelist.remove(0)class_y = []class_x = list(set(Timelist))class_x.sort()print (class_x)for i in range(len(class_x)):    class_y.append(Timelist.count(class_x[i]))print (class_y)plt.bar(class_x, class_y, label = 'class_a')plt.show()	##Ian+Goodfellow	